import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [currentSection, setCurrentSection] = useState(0);
  const intervalRef = useRef(null);
  const pauseRef = useRef(false);

  useEffect(() => {
    const startAutoScroll = () => {
      intervalRef.current = setInterval(() => {
        if (!pauseRef.current) {
          setCurrentSection(prev => (prev + 1) % 5);
        }
      }, 1500);
    };
    startAutoScroll();
    return () => clearInterval(intervalRef.current);
  }, []);

  const handleWheel = (e) => {
    e.preventDefault();
    pauseRef.current = true;
    if (e.deltaY > 0) {
      setCurrentSection(prev => (prev + 1) % 5);
    } else {
      setCurrentSection(prev => (prev - 1 + 5) % 5);
    }
    setTimeout(() => {
      pauseRef.current = false;
    }, 3000);
  };

  useEffect(() => {
    window.addEventListener('wheel', handleWheel, { passive: false });
    return () => window.removeEventListener('wheel', handleWheel);
  }, []);

  return (
    <div className="relative overflow-hidden h-screen">
      {/* Fixed Navbar */}
      <div className="fixed top-0 left-0 right-0 z-50 bg-white shadow-md">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="text-2xl font-bold text-blue-600">Budget Bae</div>
            <div className="flex gap-4">
              <Link to="/login" className="border border-blue-600 text-blue-600 px-4 py-2 rounded-lg hover:bg-blue-600 hover:text-white transition">Sign In</Link>
              <Link to="/register" className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition">Sign Up</Link>
            </div>
          </div>
        </div>
      </div>

      {/* Sections Container */}
      <div className="h-[500vh] transition-transform duration-1000 ease-in-out" style={{ transform: `translateY(-${currentSection * 100}vh)` }}>
        {/* Section 1: Hero */}
        <section className="h-screen flex items-center bg-gradient-to-br from-blue-500 to-purple-600 text-white px-4 sm:px-6 lg:px-8">
          <div className="max-w-7xl mx-auto flex flex-col lg:flex-row items-center">
            <div className="lg:w-1/2 mb-8 lg:mb-0">
              <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRLmx4nDeUICpvV543aNzvBxUTAmOk1XzE2fQ&s" alt="Expense Tracker Illustration" className="w-full max-w-lg mx-auto lg:mx-0 rounded-lg shadow-lg" />
            </div>
            <div className="lg:w-1/2 lg:pl-8 text-center lg:text-left">
              <h1 className="text-5xl sm:text-6xl font-bold mb-6">Track Your Expenses Like a Pro</h1>
              <p className="text-xl mb-8 max-w-2xl mx-auto lg:mx-0">
                Take control of your finances with our sleek, modern expense tracker. Monitor spending, set budgets, and achieve your financial goals.
              </p>
              <div className="flex flex-col sm:flex-row gap-4 justify-center lg:justify-start mb-8">
                <Link to="/register" className="bg-white text-blue-600 px-8 py-3 rounded-lg font-semibold hover:bg-gray-100 transition">Get Started</Link>
                <Link to="/login" className="border border-white text-white px-8 py-3 rounded-lg font-semibold hover:bg-white hover:text-blue-600 transition">Sign In</Link>
              </div>
              {/* Illustrations */}
              <div className="flex justify-center lg:justify-start gap-8">
                <div className="w-20 h-20 bg-white rounded-full flex items-center justify-center text-blue-600 font-bold">üí∞</div>
                <div className="w-20 h-20 bg-white rounded-full flex items-center justify-center text-blue-600 font-bold">üì±</div>
              </div>
            </div>
          </div>
        </section>

        {/* Section 2: App Preview Hero */}
        <section className="h-screen flex items-center bg-gray-100 px-4 sm:px-6 lg:px-8">
          <div className="max-w-7xl mx-auto flex flex-col lg:flex-row items-center">
            <div className="lg:w-1/2 mb-8 lg:mb-0">
              {/* App Mockup */}
              <div className="bg-white p-8 rounded-lg shadow-lg max-w-md mx-auto">
                <h3 className="text-2xl font-bold mb-4 text-gray-800">Dashboard Preview</h3>
                <div className="space-y-4">
                  <div className="bg-green-200 p-4 rounded text-center text-green-800 font-semibold">üìä Expense Chart</div>
                  <div className="bg-blue-200 p-4 rounded text-center text-blue-800 font-semibold">$1,250 / $2,000 Budget</div>
                  <div className="bg-purple-200 p-4 rounded text-center text-purple-800 font-semibold">Savings: $750</div>
                </div>
              </div>
            </div>
            <div className="lg:w-1/2 lg:pl-8 text-center lg:text-left">
              <h2 className="text-4xl font-bold mb-4 text-gray-800">Visualize Your Finances</h2>
              <p className="text-lg text-gray-600">See your spending patterns at a glance with our intuitive dashboard. Bright charts and real-time numbers keep you informed.</p>
            </div>
          </div>
        </section>

        {/* Section 3: Feature Cards + CTA */}
        <section className="h-screen flex flex-col items-center justify-center bg-gray-900 text-white px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-16 max-w-5xl">
            <div className="text-center">
              <div className="w-16 h-16 bg-blue-500 rounded-full flex items-center justify-center mx-auto mb-4 text-2xl">üìä</div>
              <h3 className="text-xl font-semibold mb-2">Expense Tracking</h3>
              <p className="text-gray-300">Log and categorize every expense effortlessly</p>
            </div>
            <div className="text-center">
              <div className="w-16 h-16 bg-blue-500 rounded-full flex items-center justify-center mx-auto mb-4 text-2xl">üí∞</div>
              <h3 className="text-xl font-semibold mb-2">Budget Management</h3>
              <p className="text-gray-300">Set limits and stay on track with your goals</p>
            </div>
            <div className="text-center">
              <div className="w-16 h-16 bg-blue-500 rounded-full flex items-center justify-center mx-auto mb-4 text-2xl">üìà</div>
              <h3 className="text-xl font-semibold mb-2">Detailed Reports</h3>
              <p className="text-gray-300">Gain insights with comprehensive analytics</p>
            </div>
          </div>
          <div className="text-center max-w-2xl">
            <h2 className="text-3xl font-bold mb-4">Ready to Take Control of Your Finances?</h2>
            <p className="text-lg mb-8 text-gray-300">Join thousands already managing money smarter</p>
            <Link to="/register" className="bg-blue-500 text-white px-8 py-3 rounded-lg font-semibold hover:bg-blue-600 transition">Start Tracking Today</Link>
          </div>
        </section>

        {/* Section 4: Testimonials */}
        <section className="h-screen flex items-center justify-center bg-white px-4 sm:px-6 lg:px-8">
          <div className="text-center max-w-5xl">
            <h2 className="text-3xl font-bold mb-8 text-gray-800">Trusted by Thousands</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
              <div className="bg-gray-100 p-6 rounded-lg">
                <div className="flex justify-center mb-4 text-yellow-400 text-2xl">‚òÖ‚òÖ‚òÖ‚òÖ‚òÖ</div>
                <p className="text-gray-700 mb-2">"This app changed how I manage my money!"</p>
                <p className="text-sm text-gray-500">- Sarah J.</p>
              </div>
              <div className="bg-gray-100 p-6 rounded-lg">
                <div className="flex justify-center mb-4 text-yellow-400 text-2xl">‚òÖ‚òÖ‚òÖ‚òÖ‚òÖ</div>
                <p className="text-gray-700 mb-2">"Easy to use and incredibly helpful."</p>
                <p className="text-sm text-gray-500">- Mike T.</p>
              </div>
              <div className="bg-blue-500 text-white p-6 rounded-lg">
                <div className="text-4xl mb-4">üë•</div>
                <p className="text-2xl font-bold">10,000+</p>
                <p>Users saving smarter every day</p>
              </div>
            </div>
          </div>
        </section>

        {/* Section 5: Footer / Final CTA */}
        <section className="h-screen flex flex-col items-center justify-center bg-gray-800 text-white px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h2 className="text-4xl font-bold mb-4">Budget Bae</h2>
            <p className="text-lg mb-8 text-gray-300">Start your journey to financial freedom today</p>
            <div className="flex flex-wrap justify-center gap-6 mb-8">
              <a href="#" className="text-blue-400 hover:text-blue-300 transition">About</a>
              <a href="#" className="text-blue-400 hover:text-blue-300 transition">Pricing</a>
              <a href="#" className="text-blue-400 hover:text-blue-300 transition">Blog</a>
              <a href="#" className="text-blue-400 hover:text-blue-300 transition">Contact</a>
            </div>
            <div className="flex justify-center gap-6 mb-8">
              <div className="w-10 h-10 bg-blue-600 rounded-full flex items-center justify-center cursor-pointer hover:bg-blue-700 transition">üìò</div>
              <div className="w-10 h-10 bg-blue-400 rounded-full flex items-center justify-center cursor-pointer hover:bg-blue-500 transition">üê¶</div>
              <div className="w-10 h-10 bg-pink-500 rounded-full flex items-center justify-center cursor-pointer hover:bg-pink-600 transition">üì∑</div>
            </div>
            <Link to="/register" className="bg-blue-500 text-white px-8 py-3 rounded-lg font-semibold hover:bg-blue-600 transition">Get Started Free</Link>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Home;
